select concat('[[',p.page_title,']]') as Article_Name,concat('[[User:',r.rev_user_text,'|',r.rev_user_text,']]') as User_Name,p.page_len as Size from revision r, page p where r.rev_parent_id = 0 and r.rev_len < 2048 and p.page_len < 2048 and p.page_namespace = 0 and p.page_is_redirect = 0 and rev_timestamp < '2015900000000' and rev_timestamp > '20150731235959' and r.rev_page = p.page_id;
